package service;

import model.Product;

import java.util.ArrayList;
import java.util.List;

public class ProductServiceImp implements ProductService{
    private static final List<Product> list = new ArrayList<>();
    static {
        list.add(new Product(1,"Tao","Ngon","https://vnn-imgs-f.vgcloud.vn/2020/12/13/13/thuc-hu-cau-noi-mot-qua-tao-giup-ban-luon-khoe-manh.jpg"));
        list.add(new Product(2,"Le","Ngon","data:image/jpeg;base64,"));
    }

    @Override
    public void save(Product product) {
        list.add(product);
    }

    @Override
    public Product findByIndex(int index) {
        return list.get(index);
    }

    @Override
    public List<Product> showAll() {
        return list;
    }

    @Override
    public void update(int id, Product product) {
        list.set(id,product);
    }

    @Override
    public void remove(int id) {
        list.remove(id);
    }

    @Override
    public Product findByName(String name) {
        for (Product p:list) {
            if(p.getName().contains(name)){
                return p;
            }
        }
        return null;
    }

    @Override
    public int findIndex(int id){
        for (int i = 0; i < list.size(); i++) {
            if(list.get(i).getId()==id){
                return i;
            }
        }
        return -1;
    }
}
